    MiHilo mh;
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String entrada = jTextField1.getText(); 
        System.out.println();
        long t1 = System.currentTimeMillis();
        try {
            int nHilos = Integer.parseInt(entrada); 
            if (nHilos == 1){
                //Funci贸n de suma secuencial
                System.out.println("HILOS: " + nHilos);
                
                MiHilo mh = new MiHilo(1, 0, 5000000, true);
                mh.start();
                mh.join();
                
                System.out.println("SUMA: " + mh.getResultado());
            }else if (nHilos > 1) {
                //Funci贸n de suma paralela
                System.out.println("HILOS: " + nHilos);
                
                if (nHilos % 2 != 0){
                    nHilos --;
                }
                
                MiHilo[] hilos = new MiHilo[nHilos];
                int intervalo = (int)Math.round(5000000 / nHilos);
                
                for (int i = 0; i < nHilos; i++){
                    hilos[i] = new MiHilo(i, (i * intervalo), ((i + 1) * intervalo) ,true);
                    hilos[i].start();
                }

                long resultado = 0;
                
                for (int i = 0; i < nHilos; i++){
                    hilos[i].join();
                    resultado += hilos[i].getResultado();
                }
                
                System.out.println("SUMA: " + resultado); 
            }
        } catch (Exception e){
            System.out.println(e);
        }
        long t2 = System.currentTimeMillis();
        System.out.println("TIEMPO: " + (t2 - t1));
    }//GEN-LAST:event_jButton1ActionPerformed

